mapper.select:
select s.STIPEND_NAME, s.STIPEND_USER_NAME, s.STIPEN_WEAL_JOURNEY, s.STIPEN_WEAL_LUNCH, s.GTIPEND_DATE, s.BASE_STIPEND, s.STIPENDTYPE_ID, s.STIPEND_WEAL, s.STATUS, s.STIPENDMANAGER_ID, t.STIPENDNAME as STIPEND_TYPE_NAME from s left join tb_stipendtype t on t.STIPENDTYPE_ID = s.STIPENDTYPE_ID where 1=1 and STATUS != 3
and s.STIPENDTYPE_ID = #{pd.STIPENDTYPE_ID}
and s.STIPENDMANAGER_ID = #{pd.STIPENDMANAGER_ID}
and (
s.STIPEND_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or s.STIPEND_USER_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and s.GTIPEND_DATE >= #{pd.lastStart}
and s.GTIPEND_DATE <= #{pd.lastEnd}

select s.STIPEND_NAME, s.STIPEND_USER_NAME, s.STIPEN_WEAL_JOURNEY, s.STIPEN_WEAL_LUNCH, s.GTIPEND_DATE, s.BASE_STIPEND, s.STIPENDTYPE_ID, s.STIPEND_WEAL, s.STATUS, s.STIPENDMANAGER_ID, t.STIPENDNAME as STIPEND_TYPE_NAME from s left join tb_stipendtype t on t.STIPENDTYPE_ID = s.STIPENDTYPE_ID where 1=1 and STATUS != 3
and s.STIPENDTYPE_ID = #{pd.STIPENDTYPE_ID}
and s.STIPENDMANAGER_ID = #{pd.STIPENDMANAGER_ID}

mapper.update:
update
set STATUS = 3 where STIPENDMANAGER_ID in
#{item}

mapper.select:
select count(*) zs from s where 1=1
and s.STIPENDMANAGER_ID = #{STIPENDMANAGER_ID}
and s.STIPENDTYPE_ID = #{pd.STIPENDTYPE_ID}
and STATUS != 3
